import { usePageOffset } from '../../use-page-offset';

import { toCamel } from '../lib/util';

import hookConfig from '../../use-page-offset/package.json';

export default function Index() {
  const { name, description, repository = {}, author = {} } = hookConfig;

  const { name: authorName, url: authorUrl } = author;

  const { url: repositoryUrl } = repository;
  const repositoryExists = typeof repositoryUrl === 'string';

  const repositoryUrlDisplay = repositoryExists && repositoryUrl.split('://')[1];

  const hookSettings = {
    message: 'Hello, custom hook!'
  }

  const { message } = usePageOffset(hookSettings);

  return (
    <main>
      <style jsx global>{`
        body {
          font-family: sans-serif;
          padding: 0;
          margin: 0;
        }

        main {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          padding: 1em 0;
        }

        h1 {
          font-size: 2em;
        }

        img {
          max-width: 100%;
        }

        pre {
          overflow: auto;
          max-height: 15em;
          background-color: #eeeeee;
          padding: 1em;
        }

        section,
        footer {
          width: 100%;
          max-width: 50em;
          margin: 0 auto;
        }

        footer p {
          font-size: .9em;
        }

        footer p,
        footer a {
          color: #546e7a;
        }
      `}</style>

      <section>

        <h1>{ toCamel(name) }</h1>

        <p>{ description }</p>

        { repositoryExists && (
          <p>
            <a href={repositoryUrl}>
              { repositoryUrlDisplay }
            </a>
          </p>
        )}

        <h2>How to use</h2>

        <p>
          Add your instructions here!
        </p>

        <h2>Examples</h2>

        <h3>Set and grab message</h3>
        <p>
          <strong>Input:</strong>
        </p>
        <pre>
          <code>
{`const hookSettings = {
  message: 'Hello, custom hook!'
}

const { message } = usePageOffset(hookSettings);`}
          </code>
        </pre>
        <p>
          <strong>Output:</strong>
        </p>
        <p>
          { message }
        </p>
      </section>

      <footer>
        <p>
          Made by <a href={authorUrl}>{ authorName }</a>
        </p>
      </footer>
    </main>
  );

}
